import { useState } from "react";

const ToDo = () => {
  const [toDos, setTodos] = useState(["Beans", "Plantain", "Gari", "Rice"]);
  const [toDo, setToDo] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    setToDos([...toDos, toDo]);
    setToDo("");
  };

  return (
    <div>
      <h1>ToDo Application</h1>
      <ul>
        {toDos.map((todo) => (
          <li>{todo}</li>
        ))}
      </ul>

      <input
        type="text"
        name="toDo"
        onChange={(e) => setToDo(e.target.value)}
      />
      <button>Add ToDo</button>
    </div>
  );
};

export default ToDo;
