import { useState } from "react";

const ToDo = () => {
  const [todoList, setTodoList] = useState([
    { id: 1, item: " Buy Apple", completed: false },
    { id: 2, item: " Buy Orange", completed: true },
    { id: 3, item: " Buy Banana", completed: false },
    { id: 4, item: " Buy Pineapple", completed: true },
  ]);
  const [todo, setTodo] = useState({});
  const handleSubmit = (e) => {
    e.preventDefault();
    let id = Math.floor(Math.random() * 10000);
    let newTodo = {
      id,
      item: todo,
      complete: false,
    };
    if (todo !== "") {
      setTodoList([...todoList, todo]);
      setTodo("");
    }
  };

  return (
    <div>
      <h1>Todo Application</h1>

      <form onSubmit={(e) => handleSubmit(e)}>
        <input
          type="text"
          name="todo"
          value={todo.item}
          onChange={(e) => setTodo(...todo, (todo.item: e.target.value))}
        />
        <button type="submit">Add Todo</button>
      </form>
      <ol>
        {todoList.map((todo) => (
          <li>{todo.item}</li>
        ))}
      </ol>
    </div>
  );
};

export default ToDo;
