import { useState } from "react";

const ToDo = () => {
  const [toDos, setToDos] = useState(["Beans", "Plantain", "Gari", "Rice"]);
  const [toDo, setToDo] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    setToDos([...toDos, toDo]);
    setToDo("");
  };

  return (
    <div>
      <h1>ToDo Application</h1>
      <ul>
        {toDos.map((toDo) => (
          <li>{toDo}</li>
        ))}
      </ul>
      <form onSubmit={(e) => handleSubmit(e)}>
        <input
          type="text"
          name="toDo"
          onChange={(e) => setToDo(e.target.value)}
        />
        <button>Add ToDo</button>
      </form>
    </div>
  );
};

export default ToDo;
