// Custom Hook
// Local Storage

import { useState } from "react";

let getorset = (key, value) => {
  const returnValue = localStorage.getItem(key);

  if (returnValue === nul) {
    localStorage.setItem(key, value);
  } else {
    return returnValue;
  }
};

getorset("name", "Prince");

const useLocalStorage = (key, value) => {
  const [val, setVal] = useState(() => getorset(key, value)); //   useState("Prince")

  return [val, setVal];
};
// login.jsx (how to call it in the login page)
// const [val, setVal] = useLocalStorage("name", "Kwame")

export default useLocalStorage;
