// Custom Hook
// Local Storage

import { useState, useEffect } from "react";

let get = (key, initialValue) => {
  const returnValue = localStorage.getItem(key);

  if (returnValue) return returnValue;

  if (initialValue instanceof Function) return initialValue();
  return initialValue;
};

const useLocalStorage = (key, initialvalue) => {
  const [val, setVal] = useState(() => get(key, initialvalue)); //   useState("Prince")

  useEffect(() => {
    localStorage.setItem(key, JSON.stringify(val));
  }, [val]);

  return [val, setVal];
};
// login.jsx (how to call it in the login page)
// const [val, setVal] = useLocalStorage("name", "Kwame")

export default useLocalStorage;
