import { useState } from "react";

const ToDo = () => {
  const [todoList, setTodoList] = useState([
    "Apple",
    "Banana",
    "Mango",
    "Orange",
    "Pineapple",
  ]);
  const [todo, setToDo] = useState("");
  console.log(todo);
  return (
    <div>
      <h1>Todo Application</h1>

      <form>
        <input
          type="text"
          name="todo"
          value={todo}
          onChange={(e) => setTodoList(e.target.value)}
        />
        <button type="submit">Add Todo</button>
      </form>
      <ol>
        {todoList.map((todo) => (
          <li>{todo}</li>
        ))}
      </ol>
    </div>
  );
};

export default ToDo;
